import React, { useEffect, useState } from 'react';
import "./css/style.css";

export const Tempapp = () => {
    
    const [city, setCity] = useState("Mumbai");
    const [search, setSearch] = useState("Mumbai");


    useEffect( () => {
        const fetchApi = async () => {
            const url = `https://api.openweathermap.org/data/2.5/weather?q=${search}&units=metric&appid=283271629120d7e58148ab79db98ab01`
            const response = await fetch(url) ;
            const resJson = response.json();

            setCity(resJson.main);
        };
        fetchApi();
    }, [search] )

  return (
      <>
    <div className='box'>
        <div className='inputData'>
            <input type="search" className='inputField' 
            onChange={(event) => {setSearch(event.target.value) }}/>
        </div>

        {!city ? (
            {/* <p> No Data Found</p> */}
        ) : (
            <div>
            <div className='info'>
          <h2 className='location'> 
          <i className="fas fa-street-view"></i>{search}
          </h2>
          <h1 className="temp">
             {city.temp}
          </h1>
           <h3 className='tempmin_max'>Min : 5025&deg;C | Max : 5.25&deg;C </h3>
    </div>

    <div className='wave -one'></div>
    <div className='wave -two'></div>
    <div className='wave -three'></div>
    </div>
        )}

     </div>

    </>
  )
}

export default Tempapp;
